// sorteo-frontend/src/components/Historico.js
import React, {useState, useEffect} from 'react';
import {toast} from 'react-toastify';
import ClipLoader from 'react-spinners/ClipLoader';
import './Historico.css';

function Historico () {
  const [resultados, setResultados] = useState ([]);
  const [cargandoResultados, setCargandoResultados] = useState (false);

  // Estados para los filtros
  const [filterSorteo, setFilterSorteo] = useState ('');
  const [filterParticipante, setFilterParticipante] = useState ('');
  const [filterPremio, setFilterPremio] = useState ('');
  const [filterFecha, setFilterFecha] = useState ('');

  const fetchResultados = async () => {
    setCargandoResultados (true);
    try {
      const response = await fetch (
        'http://127.0.0.1:8000/api/resultados_sorteo/'
      );
      const data = await response.json ();
      setResultados (data);
    } catch (error) {
      console.error ('Error al obtener resultados:', error);
      toast.error ('Error al obtener resultados.');
    } finally {
      setCargandoResultados (false);
    }
  };

  useEffect (() => {
    fetchResultados ();
  }, []);

  // Filtrar y ordenar los resultados
  const filteredResultados = resultados
    // Filtrar por nombre del sorteo
    .filter (item => {
      const sorteoNombre = item.sorteo && item.sorteo.nombre
        ? item.sorteo.nombre.toLowerCase ()
        : '';
      return sorteoNombre.includes (filterSorteo.toLowerCase ());
    })
    // Filtrar por nombre y apellido del participante
    .filter (item => {
      const participanteNombre = item.participante
        ? (item.participante.nombre +
            ' ' +
            item.participante.apellido).toLowerCase ()
        : '';
      return participanteNombre.includes (filterParticipante.toLowerCase ());
    })
    // Filtrar por nombre del premio
    .filter (item => {
      const premioNombre = item.premio && item.premio.nombre
        ? item.premio.nombre.toLowerCase ()
        : '';
      return premioNombre.includes (filterPremio.toLowerCase ());
    })
    // Filtrar por fecha (usamos la representación local en minúsculas)
    .filter (item => {
      const fechaStr = item.fecha
        ? new Date (item.fecha).toLocaleString ().toLowerCase ()
        : '';
      return fechaStr.includes (filterFecha.toLowerCase ());
    })
    // Ordenar de lo más nuevo a lo más viejo (descendente por fecha)
    .sort ((a, b) => new Date (b.fecha) - new Date (a.fecha));

  return (
    <div className="historico-container">
      <h2>Histórico de Sorteos y Actividades</h2>

      {/* Sección de filtros */}
      <div className="filter-section">
        <label>
          Sorteo:
          <input
            type="text"
            value={filterSorteo}
            onChange={e => setFilterSorteo (e.target.value)}
            placeholder="Filtrar por sorteo"
          />
        </label>
        <label>
          Participante:
          <input
            type="text"
            value={filterParticipante}
            onChange={e => setFilterParticipante (e.target.value)}
            placeholder="Filtrar por participante"
          />
        </label>
        <label>
          Premio:
          <input
            type="text"
            value={filterPremio}
            onChange={e => setFilterPremio (e.target.value)}
            placeholder="Filtrar por premio"
          />
        </label>
        <label>
          Fecha:
          <input
            type="text"
            value={filterFecha}
            onChange={e => setFilterFecha (e.target.value)}
            placeholder="Filtrar por fecha"
          />
        </label>
      </div>

      {/* Lista de resultados */}
      <div className="historico-section">
        <h3>Resultados de Sorteos</h3>
        {cargandoResultados
          ? <ClipLoader size={50} color="#123abc" />
          : <table className="historico-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Sorteo</th>
                  <th>Participante</th>
                  <th>Premio</th>
                  <th>Fecha</th>
                </tr>
              </thead>
              <tbody>
                {filteredResultados.map (resultado => (
                  <tr key={resultado.id}>
                    <td>{resultado.id}</td>
                    <td>
                      {resultado.sorteo && resultado.sorteo.nombre
                        ? resultado.sorteo.nombre
                        : 'Sin nombre'}
                    </td>
                    <td>
                      {(resultado.participante &&
                        resultado.participante.nombre) ||
                        'Sin participante'}
                      {' '}
                      {(resultado.participante &&
                        resultado.participante.apellido) ||
                        ''}
                    </td>
                    <td>
                      {resultado.premio && resultado.premio.nombre
                        ? resultado.premio.nombre
                        : 'Sin premio'}
                    </td>
                    <td>{new Date (resultado.fecha).toLocaleString ()}</td>
                  </tr>
                ))}
              </tbody>
            </table>}
      </div>
    </div>
  );
}

export default Historico;
