// sorteo-frontend/src/components/Bases.js

import React, {useState, useEffect} from 'react';
import {toast} from 'react-toastify';
import ClipLoader from 'react-spinners/ClipLoader';
import {API_BASE_URL} from '../config';
import UploadCSV from './UploadCSV';
import AddToBlacklist from './AddToBlacklist';
import AddToParticipants from './AddToParticipants';
import './Bases.css';

function Bases () {
  // Estado para controlar la pestaña activa:
  // 'participantes' → Listado participantes
  // 'no_incluidos' → Listado No incluidos
  // 'cargar' → Cargar bases
  const [activeTab, setActiveTab] = useState ('participantes');
  const [data, setData] = useState (null);
  const [loading, setLoading] = useState (false);

  const fetchLists = async () => {
    setLoading (true);
    try {
      const response = await fetch (`${API_BASE_URL}/api/lists/`);
      const jsonData = await response.json ();
      setData (jsonData);
    } catch (error) {
      console.error (error);
      toast.error ('Error al obtener las bases.');
    } finally {
      setLoading (false);
    }
  };

  useEffect (() => {
    fetchLists ();
  }, []);

  // Paginación (si la usás, aquí se puede ajustar)
  const itemsPerPage = 100;
  const paginatedParticipantes = data && data.participantes
    ? data.participantes.slice (0, itemsPerPage)
    : [];
  const paginatedNoIncluidos = data && data.blacklist
    ? data.blacklist.slice (0, itemsPerPage)
    : [];

  return (
    <div className="bases-container">
      <h2>Bases</h2>
      <div className="tabs">
        <button
          className={activeTab === 'participantes' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab ('participantes')}
        >
          Listado participantes
        </button>
        <button
          className={activeTab === 'no_incluidos' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab ('no_incluidos')}
        >
          Listado No incluidos
        </button>
        <button
          className={activeTab === 'cargar' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab ('cargar')}
        >
          Cargar bases
        </button>
      </div>

      {activeTab === 'participantes' &&
        <div className="list-section">
          {loading
            ? <ClipLoader size={50} color="#123abc" />
            : data && data.participantes && data.participantes.length > 0
                ? <table className="table">
                    <thead>
                      <tr>
                        <th>Legajo</th>
                        <th>Nombre</th>
                        <th>Apellido</th>
                        <th>Área</th>
                        <th>Dominio</th>
                        <th>Cargo</th>
                        <th>Email</th>
                        <th>Localidad</th>
                        <th>Provincia</th>
                        <th>Acciones</th>
                      </tr>
                    </thead>
                    <tbody>
                      {paginatedParticipantes.map (item => (
                        <tr key={item.id}>
                          <td>{item.id}</td>
                          <td>{item.nombre}</td>
                          <td>{item.apellido}</td>
                          <td>{item.area}</td>
                          <td>{item.dominio}</td>
                          <td>{item.cargo}</td>
                          <td>{item.email}</td>
                          <td>{item.localidad}</td>
                          <td>{item.provincia}</td>
                          <td>
                            <button
                              onClick={async () => {
                                try {
                                  const response = await fetch (
                                    `${API_BASE_URL}/api/blacklist/add/`,
                                    {
                                      method: 'POST',
                                      headers: {
                                        'Content-Type': 'application/json',
                                      },
                                      body: JSON.stringify ({id: item.id}),
                                    }
                                  );
                                  const data = await response.json ();
                                  if (response.ok) {
                                    toast.success (data.message);
                                    fetchLists ();
                                  } else {
                                    toast.error (
                                      data.error ||
                                        'Error al mover el registro.'
                                    );
                                  }
                                } catch (err) {
                                  console.error (err);
                                  toast.error ('Error de conexión.');
                                }
                              }}
                            >
                              Mover a No incluidos
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                : <p>No se encontraron registros de participantes.</p>}
        </div>}

      {activeTab === 'no_incluidos' &&
        <div className="list-section">
          {loading
            ? <ClipLoader size={50} color="#123abc" />
            : data && data.blacklist && data.blacklist.length > 0
                ? <table className="table">
                    <thead>
                      <tr>
                        <th>Legajo</th>
                        <th>Nombre</th>
                        <th>Apellido</th>
                        <th>Área</th>
                        <th>Dominio</th>
                        <th>Cargo</th>
                        <th>Email</th>
                        <th>Localidad</th>
                        <th>Provincia</th>
                        <th>Acciones</th>
                      </tr>
                    </thead>
                    <tbody>
                      {paginatedNoIncluidos.map (item => (
                        <tr key={item.id}>
                          <td>{item.id}</td>
                          <td>{item.nombre}</td>
                          <td>{item.apellido}</td>
                          <td>{item.area}</td>
                          <td>{item.dominio}</td>
                          <td>{item.cargo}</td>
                          <td>{item.email}</td>
                          <td>{item.localidad}</td>
                          <td>{item.provincia}</td>
                          <td>
                            <button
                              onClick={async () => {
                                try {
                                  const payload = {
                                    id: item.id,
                                    nombre: item.nombre,
                                    apellido: item.apellido,
                                    email: item.email,
                                    area: item.area,
                                    dominio: item.dominio,
                                    cargo: item.cargo,
                                    localidad: item.localidad,
                                    provincia: item.provincia,
                                  };
                                  const response = await fetch (
                                    `${API_BASE_URL}/api/participants/add/`,
                                    {
                                      method: 'POST',
                                      headers: {
                                        'Content-Type': 'application/json',
                                      },
                                      body: JSON.stringify (payload),
                                    }
                                  );
                                  const data = await response.json ();
                                  if (response.ok) {
                                    toast.success (data.message);
                                    fetchLists ();
                                  } else {
                                    toast.error (
                                      data.error ||
                                        'Error al mover el registro.'
                                    );
                                  }
                                } catch (err) {
                                  console.error (err);
                                  toast.error ('Error de conexión.');
                                }
                              }}
                            >
                              Mover a Participantes
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                : <p>
                    No se encontraron registros en la lista de No incluidos.
                  </p>}
        </div>}

      {activeTab === 'cargar' &&
        <div className="cargar-section">
          <div className="cargar-item">
            <h3>Cargar CSV</h3>
            <UploadCSV onUpdate={fetchLists} />
          </div>
          <div className="cargar-item">
            <h3>Agregar a Lista No incluidos</h3>
            <AddToBlacklist onUpdate={fetchLists} />
          </div>
          <div className="cargar-item">
            <h3>Agregar Participante Manualmente</h3>
            <AddToParticipants onUpdate={fetchLists} />
          </div>
        </div>}
    </div>
  );
}

export default Bases;
