// sorteo-frontend/src/components/Bases.js
import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import ClipLoader from 'react-spinners/ClipLoader';
import { API_BASE_URL } from '../config';
import UploadCSV from './UploadCSV';
import AddToBlacklist from './AddToBlacklist';
import AddToParticipants from './AddToParticipants';
import './Bases.css';

function Bases() {
  // Estado de pestaña activa: 'participantes', 'no_incluidos', 'cargar'
  const [activeTab, setActiveTab] = useState('participantes');
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  // Estados para paginación
  const itemsPerPage = 100;
  const [currentPageParticipantes, setCurrentPageParticipantes] = useState(1);
  const [currentPageNoIncluidos, setCurrentPageNoIncluidos] = useState(1);

  // Estado para edición
  // editingRecord: objeto con los datos que se están editando (incluye id y demás campos)
  // editingList: 'participantes' o 'blacklist'
  const [editingRecord, setEditingRecord] = useState(null);
  const [editingList, setEditingList] = useState(null);

  // Definir los íconos SVG para los botones
  const incluyeIcon = (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512" width="16" height="16" fill="#fff">
      <path d="M438.6 105.4c12.5 12.5 12.5 32.8 0 45.3l-256 256c-12.5 12.5-32.8 12.5-45.3 0l-128-128c-12.5-12.5-12.5-32.8 0-45.3s32.8-12.5 45.3 0L160 338.7 393.4 105.4c12.5-12.5 32.8-12.5 45.3 0z"/>
    </svg>
  );
  const noIncluyeIcon = (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 384 512" width="16" height="16" fill="#fff">
      <path d="M342.6 150.6c12.5-12.5 12.5-32.8 0-45.3s-32.8-12.5-45.3 0L192 210.7 86.6 105.4c-12.5-12.5-32.8-12.5-45.3 0s-12.5 32.8 0 45.3L146.7 256 41.4 361.4c-12.5 12.5-12.5 32.8 0 45.3s32.8 12.5 45.3 0L192 301.3 297.4 406.6c12.5 12.5 32.8 12.5 45.3 0s12.5-32.8 0-45.3L237.3 256 342.6 150.6z"/>
    </svg>
  );
  const editIcon = (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" width="16" height="16" fill="#fff">
      <path d="M441 58.9L453.1 71c9.4 9.4 9.4 24.6 0 33.9L424 134.1 377.9 88 407 58.9c9.4-9.4 24.6-9.4 33.9 0zM209.8 256.2L344 121.9 390.1 168 255.8 302.2c-2.9 2.9-6.5 5-10.4 6.1l-58.5 16.7 16.7-58.5c1.1-3.9 3.2-7.5 6.1-10.4zM373.1 25L175.8 222.2c-8.7 8.7-15 19.4-18.3 31.1l-28.6 100c-2.4 8.4-.1 17.4 6.1 23.6s15.2 8.5 23.6 6.1l100-28.6c11.8-3.4 22.5-9.7 31.1-18.3L487 138.9c28.1-28.1 28.1-73.7 0-101.8L474.9 25C446.8-3.1 401.2-3.1 373.1 25zM88 64C39.4 64 0 103.4 0 152L0 424c0 48.6 39.4 88 88 88l272 0c48.6 0 88-39.4 88-88l0-112c0-13.3-10.7-24-24-24s-24 10.7-24 24l0 112c0 22.1-17.9 40-40 40L88 464c-22.1 0-40-17.9-40-40l0-272c0-22.1 17.9-40 40-40l112 0c13.3 0 24-10.7 24-24s-10.7-24-24-24L88 64z"/>
    </svg>
  );

  // Función para obtener las listas
  const fetchLists = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/api/lists/`);
      const jsonData = await response.json();
      setData(jsonData);
    } catch (error) {
      console.error(error);
      toast.error('Error al obtener las bases.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLists();
  }, []);

  // Paginación para participantes
  const totalRecordsParticipantes = data && data.participantes ? data.participantes.length : 0;
  const totalPagesParticipantes = Math.ceil(totalRecordsParticipantes / itemsPerPage);
  const paginatedParticipantes = data && data.participantes
    ? data.participantes.slice(
        (currentPageParticipantes - 1) * itemsPerPage,
        currentPageParticipantes * itemsPerPage
      )
    : [];

  const handleNextParticipantes = () => {
    if (currentPageParticipantes < totalPagesParticipantes)
      setCurrentPageParticipantes((prev) => prev + 1);
  };

  const handlePrevParticipantes = () => {
    if (currentPageParticipantes > 1)
      setCurrentPageParticipantes((prev) => prev - 1);
  };

  // Paginación para no incluidos
  const totalRecordsNoIncluidos = data && data.blacklist ? data.blacklist.length : 0;
  const totalPagesNoIncluidos = Math.ceil(totalRecordsNoIncluidos / itemsPerPage);
  const paginatedNoIncluidos = data && data.blacklist
    ? data.blacklist.slice(
        (currentPageNoIncluidos - 1) * itemsPerPage,
        currentPageNoIncluidos * itemsPerPage
      )
    : [];

  const handleNextNoIncluidos = () => {
    if (currentPageNoIncluidos < totalPagesNoIncluidos)
      setCurrentPageNoIncluidos((prev) => prev + 1);
  };

  const handlePrevNoIncluidos = () => {
    if (currentPageNoIncluidos > 1)
      setCurrentPageNoIncluidos((prev) => prev - 1);
  };

  // Handler para mover un registro de participantes a no incluidos (actualiza estado local inmediatamente)
  const handleNoIncluir = async (participante) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/blacklist/add/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(participante),
      });
      const respData = await response.json();
      if (response.ok) {
        toast.success(respData.message);
        setData((prevData) => ({
          ...prevData,
          participantes: prevData.participantes.filter(p => p.id !== participante.id),
          blacklist: [...(prevData.blacklist || []), participante],
        }));
      } else {
        toast.error(respData.error || 'Error al mover a no incluidos.');
      }
    } catch (error) {
      console.error(error);
      toast.error('Error de conexión.');
    }
  };

  // Handler para mover un registro de no incluidos a participantes
  const handleIncluir = async (legajo) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/blacklist/remove/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: legajo }),
      });
      const respData = await response.json();
      if (response.ok) {
        toast.success(respData.message);
        // Se actualiza llamando nuevamente a fetchLists()
        fetchLists();
      } else {
        toast.error(respData.error || 'Error al mover a participantes.');
      }
    } catch (error) {
      console.error(error);
      toast.error('Error de conexión.');
    }
  };

  // Handler para iniciar la edición de un registro
  const handleEdit = (record, listType) => {
    setEditingRecord({ ...record }); // Clonamos el objeto
    setEditingList(listType);
  };

  // Handler para cancelar la edición
  const handleCancelEdit = () => {
    setEditingRecord(null);
    setEditingList(null);
  };

  // Handler para actualizar los cambios en el registro editado
  const handleChangeEdit = (e) => {
    const { name, value } = e.target;
    setEditingRecord((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handler para guardar los cambios (envía PUT)
  const handleSaveEdit = async () => {
    if (!editingRecord) return;
    const endpoint =
      editingList === 'participantes'
        ? `${API_BASE_URL}/api/participants/${editingRecord.id}/`
        : `${API_BASE_URL}/api/blacklist/${editingRecord.id}/`;
    try {
      const response = await fetch(endpoint, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingRecord),
      });
      const respData = await response.json();
      if (response.ok) {
        toast.success(respData.message || 'Registro actualizado.');
        // Actualiza las listas: para simplicidad, volvemos a llamar a fetchLists()
        fetchLists();
        setEditingRecord(null);
        setEditingList(null);
      } else {
        toast.error(respData.error || 'Error al actualizar.');
      }
    } catch (error) {
      console.error(error);
      toast.error('Error de conexión.');
    }
  };

  // Función para renderizar una fila (modo vista o modo edición)
  const renderRow = (item, listType) => {
    if (editingRecord && editingRecord.id === item.id) {
      // Modo edición: mostrar inputs
      return (
        <tr key={item.id}>
          <td>{item.id}</td>
          <td><input type="text" name="nombre" value={editingRecord.nombre} onChange={handleChangeEdit} /></td>
          <td><input type="text" name="apellido" value={editingRecord.apellido} onChange={handleChangeEdit} /></td>
          <td><input type="text" name="area" value={editingRecord.area} onChange={handleChangeEdit} /></td>
          <td><input type="text" name="dominio" value={editingRecord.dominio} onChange={handleChangeEdit} /></td>
          <td><input type="text" name="cargo" value={editingRecord.cargo} onChange={handleChangeEdit} /></td>
          <td><input type="email" name="email" value={editingRecord.email} onChange={handleChangeEdit} /></td>
          <td><input type="text" name="localidad" value={editingRecord.localidad} onChange={handleChangeEdit} /></td>
          <td><input type="text" name="provincia" value={editingRecord.provincia} onChange={handleChangeEdit} /></td>
          <td>
            <button className="accion-btn" onClick={handleSaveEdit}>Guardar</button>
            <button className="accion-btn cancelar" onClick={handleCancelEdit}>Cancelar</button>
          </td>
        </tr>
      );
    } else {
      // Modo vista: mostrar valores y botones de acciones
      return (
        <tr key={item.id}>
          <td>{item.id}</td>
          <td>{item.nombre}</td>
          <td>{item.apellido}</td>
          <td>{item.area}</td>
          <td>{item.dominio}</td>
          <td>{item.cargo}</td>
          <td>{item.email}</td>
          <td>{item.localidad}</td>
          <td>{item.provincia}</td>
          <td>
            {listType === 'participantes' ? (
              <>
                <button className="accion-btn" onClick={() => handleNoIncluir(item)}>
                  {noIncluyeIcon}
                </button>
                <button className="accion-btn" onClick={() => handleEdit(item, 'participantes')}>
                  {editIcon}
                </button>
              </>
            ) : (
              <>
                <button className="accion-btn" onClick={() => handleIncluir(item.id)}>
                  {incluyeIcon}
                </button>
                <button className="accion-btn" onClick={() => handleEdit(item, 'blacklist')}>
                  {editIcon}
                </button>
              </>
            )}
          </td>
        </tr>
      );
    }
  };

  return (
    <div className="bases-container">
      <h2>Bases</h2>
      <div className="tabs">
        <button
          className={activeTab === 'participantes' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('participantes')}
        >
          Listado participantes
        </button>
        <button
          className={activeTab === 'no_incluidos' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('no_incluidos')}
        >
          Listado No incluidos
        </button>
        <button
          className={activeTab === 'cargar' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('cargar')}
        >
          Cargar bases
        </button>
      </div>

      {/* Lista de participantes */}
      {activeTab === 'participantes' && (
        <div className="list-section">
          {loading ? (
            <ClipLoader size={50} color="#123abc" />
          ) : data && data.participantes && data.participantes.length > 0 ? (
            <>
              <table className="table">
                <thead>
                  <tr>
                    <th>Legajo</th>
                    <th>Nombre</th>
                    <th>Apellido</th>
                    <th>Área</th>
                    <th>Dominio</th>
                    <th>Cargo</th>
                    <th>Email</th>
                    <th>Localidad</th>
                    <th>Provincia</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {paginatedParticipantes.map((item) => renderRow(item, 'participantes'))}
                </tbody>
              </table>
              {totalPagesParticipantes > 1 && (
                <div className="pagination">
                  <button onClick={handlePrevParticipantes} disabled={currentPageParticipantes === 1}>
                    Anterior
                  </button>
                  <span>
                    Página {currentPageParticipantes} de {totalPagesParticipantes}
                  </span>
                  <button
                    onClick={handleNextParticipantes}
                    disabled={currentPageParticipantes === totalPagesParticipantes}
                  >
                    Siguiente
                  </button>
                </div>
              )}
            </>
          ) : (
            <p>No se encontraron registros de participantes.</p>
          )}
        </div>
      )}

      {/* Lista de no incluidos */}
      {activeTab === 'no_incluidos' && (
        <div className="list-section">
          {loading ? (
            <ClipLoader size={50} color="#123abc" />
          ) : data && data.blacklist && data.blacklist.length > 0 ? (
            <>
              <table className="table">
                <thead>
                  <tr>
                    <th>Legajo</th>
                    <th>Nombre</th>
                    <th>Apellido</th>
                    <th>Área</th>
                    <th>Dominio</th>
                    <th>Cargo</th>
                    <th>Email</th>
                    <th>Localidad</th>
                    <th>Provincia</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {paginatedNoIncluidos.map((item) => renderRow(item, 'blacklist'))}
                </tbody>
              </table>
              {totalPagesNoIncluidos > 1 && (
                <div className="pagination">
                  <button onClick={handlePrevNoIncluidos} disabled={currentPageNoIncluidos === 1}>
                    Anterior
                  </button>
                  <span>
                    Página {currentPageNoIncluidos} de {totalPagesNoIncluidos}
                  </span>
                  <button
                    onClick={handleNextNoIncluidos}
                    disabled={currentPageNoIncluidos === totalPagesNoIncluidos}
                  >
                    Siguiente
                  </button>
                </div>
              )}
            </>
          ) : (
            <p>No se encontraron registros en el listado de No incluidos.</p>
          )}
        </div>
      )}

      {/* Sección de cargar bases */}
      {activeTab === 'cargar' && (
        <div className="cargar-section">
          <div className="cargar-item">
            <h3>Cargar CSV</h3>
            <UploadCSV />
          </div>
          <div className="cargar-item">
            <h3>Agregar individualmente a la lista de no incluidos</h3>
            <AddToBlacklist />
          </div>
          <div className="cargar-item">
            <h3>Agregar individualmente a la lista de participantes</h3>
            <AddToParticipants />
          </div>
        </div>
      )}
    </div>
  );
}

export default Bases;
