// sorteo-frontend/src/components/ListasCargadas.js

import React, {useState, useEffect} from 'react';
import {toast} from 'react-toastify';
import ClipLoader from 'react-spinners/ClipLoader';
import './ListasCargadas.css';

function ListasCargadas () {
  const [datos, setDatos] = useState (null);
  const [cargando, setCargando] = useState (false);
  const [borrando, setBorrando] = useState (false);

  const fetchLists = async () => {
    setCargando (true);
    try {
      const response = await fetch ('http://127.0.0.1:8000/api/lists/');
      const data = await response.json ();
      setDatos (data);
    } catch (error) {
      toast.error ('Error al obtener las listas.');
    } finally {
      setCargando (false);
    }
  };

  const clearLists = async () => {
    if (!window.confirm ('¿Estás seguro de borrar todas las listas cargadas?'))
      return;
    setBorrando (true);
    try {
      const response = await fetch ('http://127.0.0.1:8000/api/lists/clear/', {
        method: 'DELETE',
      });
      const data = await response.json ();
      toast.info (data.message);
      setDatos (null);
    } catch (error) {
      toast.error ('Error al borrar las listas.');
    } finally {
      setBorrando (false);
    }
  };

  useEffect (() => {
    fetchLists ();
  }, []);

  return (
    <div className="listas-container">
      <h2>Listas Cargadas</h2>
      <button onClick={fetchLists}>Refrescar Listas</button>
      <button onClick={clearLists} disabled={borrando}>
        {borrando ? 'Borrando...' : 'Borrar Listas'}
      </button>
      {cargando
        ? <ClipLoader size={50} color="#123abc" />
        : datos
            ? <div className="listas-contenido">
                <div>
                  <h3>Participantes</h3>
                  {datos.participantes.length > 0
                    ? <ul>
                        {datos.participantes.map (p => (
                          <li key={p.id}>
                            {p.id}
                            {' '}
                            -
                            {' '}
                            {p.nombre}
                            {' '}
                            {p.apellido}
                            {' '}
                            (
                            {p.email}
                            ) -
                            {' '}
                            {p.localidad}
                            ,
                            {' '}
                            {p.provincia}
                          </li>
                        ))}
                      </ul>
                    : <p>No hay participantes cargados.</p>}
                </div>
                <div>
                  <h3>Lista Negra</h3>
                  {datos.blacklist.length > 0
                    ? <ul>
                        {datos.blacklist.map (item => (
                          <li key={item.id}>ID: {item.user_id}</li>
                        ))}
                      </ul>
                    : <p>No hay IDs en la lista negra.</p>}
                </div>
              </div>
            : <p>No se encontraron datos.</p>}
    </div>
  );
}

export default ListasCargadas;
