// sorteo-frontend/src/components/Historico.js

import React, {useState, useEffect} from 'react';
import {toast} from 'react-toastify';
import ClipLoader from 'react-spinners/ClipLoader';
import './Historico.css';

function Historico () {
  const [sorteos, setSorteos] = useState ([]);
  const [resultados, setResultados] = useState ([]);
  const [actividad, setActividad] = useState ([]);
  const [cargandoSorteos, setCargandoSorteos] = useState (false);
  const [cargandoResultados, setCargandoResultados] = useState (false);
  const [cargandoActividad, setCargandoActividad] = useState (false);

  // Estados para filtros
  const [filtroSorteo, setFiltroSorteo] = useState ('');
  const [filtroParticipante, setFiltroParticipante] = useState ('');
  const [filtroPremio, setFiltroPremio] = useState ('');
  const [filtroFecha, setFiltroFecha] = useState ('');

  // Opciones de filtro (desplegables)
  const [opcionesSorteo, setOpcionesSorteo] = useState ([]);
  const [opcionesParticipante, setOpcionesParticipante] = useState ([]);
  const [opcionesPremio, setOpcionesPremio] = useState ([]);
  const [opcionesFecha, setOpcionesFecha] = useState ([]);

  // --- FETCH DE DATOS ---

  // Fetch Sorteos (para el listado de sorteos históricos)
  const fetchSorteos = async () => {
    setCargandoSorteos (true);
    try {
      const response = await fetch ('http://127.0.0.1:8000/api/sorteos/');
      const data = await response.json ();
      // Ordenar de más nuevo a más viejo (suponiendo que "fecha_hora" es la fecha)
      const sorteosOrdenados = data.sort (
        (a, b) => new Date (b.fecha_hora) - new Date (a.fecha_hora)
      );
      setSorteos (sorteosOrdenados);
    } catch (error) {
      console.error ('Error al obtener sorteos:', error);
      toast.error ('Error al obtener sorteos.');
    } finally {
      setCargandoSorteos (false);
    }
  };

  // Fetch Resultados (sorteos realizados)
  const fetchResultados = async () => {
    setCargandoResultados (true);
    try {
      const response = await fetch (
        'http://127.0.0.1:8000/api/resultados_sorteo/'
      );
      const data = await response.json ();
      // Ordenar de más nuevo a más viejo (suponiendo que "fecha" es la fecha del resultado)
      const resultadosOrdenados = data.sort (
        (a, b) => new Date (b.fecha) - new Date (a.fecha)
      );
      setResultados (resultadosOrdenados);
    } catch (error) {
      console.error ('Error al obtener resultados:', error);
      toast.error ('Error al obtener resultados.');
    } finally {
      setCargandoResultados (false);
    }
  };

  // Fetch Registro de Actividad
  const fetchActividad = async () => {
    setCargandoActividad (true);
    try {
      const response = await fetch (
        'http://127.0.0.1:8000/api/registro_actividad/'
      );
      const data = await response.json ();
      setActividad (data);
    } catch (error) {
      console.error ('Error al obtener actividad:', error);
      toast.error ('Error al obtener actividad.');
    } finally {
      setCargandoActividad (false);
    }
  };

  useEffect (() => {
    fetchSorteos ();
    fetchResultados ();
    fetchActividad ();
  }, []);

  // --- GENERAR OPCIONES DE FILTRO A PARTIR DE LOS RESULTADOS ---
  useEffect (
    () => {
      // Para sorteo: obtener nombres únicos del sorteo
      const nombresSorteo = Array.from (
        new Set (
          resultados.map (r => r.sorteo && r.sorteo.nombre).filter (Boolean)
        )
      );
      setOpcionesSorteo (nombresSorteo);

      // Para participante: obtener nombres y apellidos únicos
      const nombresParticipante = Array.from (
        new Set (
          resultados
            .map (
              r =>
                r.participante
                  ? `${r.participante.nombre} ${r.participante.apellido}`
                  : ''
            )
            .filter (Boolean)
        )
      );
      setOpcionesParticipante (nombresParticipante);

      // Para premio: obtener nombres únicos
      const nombresPremio = Array.from (
        new Set (
          resultados.map (r => r.premio && r.premio.nombre).filter (Boolean)
        )
      );
      setOpcionesPremio (nombresPremio);

      // Para fecha: obtener fechas únicas (en formato YYYY-MM-DD)
      const fechasUnicas = Array.from (
        new Set (
          resultados
            .map (r => {
              const d = new Date (r.fecha);
              // Formatear a YYYY-MM-DD
              return `${d.getFullYear ()}-${('0' + (d.getMonth () + 1)).slice (-2)}-${('0' + d.getDate ()).slice (-2)}`;
            })
            .filter (Boolean)
        )
      );
      setOpcionesFecha (fechasUnicas);
    },
    [resultados]
  );

  // --- FILTRADO DE RESULTADOS ---
  const resultadosFiltrados = resultados.filter (r => {
    // Filtro por sorteo
    const matchSorteo = filtroSorteo
      ? r.sorteo && r.sorteo.nombre === filtroSorteo
      : true;
    // Filtro por participante (comparar el string concatenado)
    const participanteNombre = r.participante
      ? `${r.participante.nombre} ${r.participante.apellido}`
      : '';
    const matchParticipante = filtroParticipante
      ? participanteNombre === filtroParticipante
      : true;
    // Filtro por premio
    const matchPremio = filtroPremio
      ? r.premio && r.premio.nombre === filtroPremio
      : true;
    // Filtro por fecha: convertimos la fecha del resultado al formato YYYY-MM-DD
    const d = new Date (r.fecha);
    const fechaFormateada = `${d.getFullYear ()}-${('0' + (d.getMonth () + 1)).slice (-2)}-${('0' + d.getDate ()).slice (-2)}`;
    const matchFecha = filtroFecha ? fechaFormateada === filtroFecha : true;
    return matchSorteo && matchParticipante && matchPremio && matchFecha;
  });

  return (
    <div className="historico-container">
      <h2>Histórico de Sorteos y Actividades</h2>

      {/* Filtros */}
      <div className="filtros-container">
        <div className="filtro">
          <label>Sorteo:</label>
          <select
            value={filtroSorteo}
            onChange={e => setFiltroSorteo (e.target.value)}
          >
            <option value="">Todos</option>
            {opcionesSorteo.map ((nombre, idx) => (
              <option key={idx} value={nombre}>{nombre}</option>
            ))}
          </select>
        </div>
        <div className="filtro">
          <label>Participante:</label>
          <select
            value={filtroParticipante}
            onChange={e => setFiltroParticipante (e.target.value)}
          >
            <option value="">Todos</option>
            {opcionesParticipante.map ((nombre, idx) => (
              <option key={idx} value={nombre}>{nombre}</option>
            ))}
          </select>
        </div>
        <div className="filtro">
          <label>Premio:</label>
          <select
            value={filtroPremio}
            onChange={e => setFiltroPremio (e.target.value)}
          >
            <option value="">Todos</option>
            {opcionesPremio.map ((nombre, idx) => (
              <option key={idx} value={nombre}>{nombre}</option>
            ))}
          </select>
        </div>
        <div className="filtro">
          <label>Fecha:</label>
          <select
            value={filtroFecha}
            onChange={e => setFiltroFecha (e.target.value)}
          >
            <option value="">Todas</option>
            {opcionesFecha.map ((fecha, idx) => (
              <option key={idx} value={fecha}>{fecha}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Listado de Resultados (Sorteos realizados) */}
      <div className="historico-section">
        <h3>Resultados de Sorteos</h3>
        {cargandoResultados
          ? <ClipLoader size={50} color="#123abc" />
          : <table className="historico-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Sorteo</th>
                  <th>Participante</th>
                  <th>Premio</th>
                  <th>Fecha</th>
                </tr>
              </thead>
              <tbody>
                {resultadosFiltrados.map (resultado => (
                  <tr key={resultado.id}>
                    <td>{resultado.id}</td>
                    <td>
                      {resultado.sorteo && resultado.sorteo.nombre
                        ? resultado.sorteo.nombre
                        : 'Sin nombre'}
                    </td>
                    <td>
                      {(resultado.participante &&
                        resultado.participante.nombre) ||
                        'Sin participante'}
                      {' '}
                      {(resultado.participante &&
                        resultado.participante.apellido) ||
                        ''}
                    </td>
                    <td>
                      {resultado.premio && resultado.premio.nombre
                        ? resultado.premio.nombre
                        : 'Sin premio'}
                    </td>
                    <td>{new Date (resultado.fecha).toLocaleString ()}</td>
                  </tr>
                ))}
              </tbody>
            </table>}
      </div>

      <hr />

      {/* Listado de Sorteos (histórico general, sin filtros) */}
      <div className="historico-section">
        <h3>Lista de Sorteos</h3>
        {cargandoSorteos
          ? <ClipLoader size={50} color="#123abc" />
          : <table className="historico-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Nombre</th>
                  <th>Descripción</th>
                  <th>Fecha y Hora</th>
                </tr>
              </thead>
              <tbody>
                {sorteos.map (sorteo => (
                  <tr key={sorteo.id}>
                    <td>{sorteo.id}</td>
                    <td>{sorteo.nombre || 'Sin nombre'}</td>
                    <td>{sorteo.descripcion || '-'}</td>
                    <td>{new Date (sorteo.fecha_hora).toLocaleString ()}</td>
                  </tr>
                ))}
              </tbody>
            </table>}
      </div>

      <hr />

      {/* Registro de Actividades */}
      <div className="historico-section">
        <h3>Registro de Actividades</h3>
        {cargandoActividad
          ? <ClipLoader size={50} color="#123abc" />
          : <ul className="actividad-list">
              {actividad.map (act => (
                <li key={act.id}>
                  {new Date (act.fecha_hora).toLocaleString ()} - {act.evento}
                </li>
              ))}
            </ul>}
      </div>
    </div>
  );
}

export default Historico;
